		    flash[:error]="category not updated"      
		    render "edit"
    	end
	end

	def destroy
	    @category = Category.find(params[:id]) 
	    if @category.destroy
	    	flash[:notice]="category deleted scuccessfully"
	    else
	    	flahs[:error]="category not deleted"
	    end
	    redirect_to admin_categories_url
	end
end